mapper.select:
SELECT ba.id, ba.item_id AS itemId, i.name AS itemName, ii.image_url AS itemImage, ba.user_id AS userId, u.real_name AS username, ba.expected_borrow_time AS expectedBorrowTime, ba.expected_return_time AS expectedReturnTime, ba.status, ba.apply_time AS applyTime, ba.update_time AS auditTime, ba.reject_reason AS rejectReason FROM borrow_apply ba LEFT JOIN item i ON ba.item_id = i.id AND i.is_delete = 0 LEFT JOIN item_image ii ON i.id = ii.item_id AND ii.is_main = 1 LEFT JOIN user u ON ba.user_id = u.id AND u.is_delete = 0 WHERE 1=1
AND ba.user_id = #{userId}
AND ba.status = #{status}
ORDER BY ba.apply_time DESC

SELECT COUNT(DISTINCT ba.id) FROM borrow_apply ba LEFT JOIN item i ON ba.item_id = i.id AND i.is_delete = 0 LEFT JOIN user u ON ba.user_id = u.id AND u.is_delete = 0 WHERE 1=1
AND ba.user_id = #{userId}
AND ba.status = #{status}

SELECT COUNT(*) FROM borrow_apply
WHERE item_id = #{itemId}
  AND user_id = #{userId}
  AND status IN (#{status1}, #{status2})
